import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import ReactMultiSelectCheckboxes from 'react-multiselect-checkboxes';
// import Select from 'react-select';

function Product(props){
  return(
    <div className="card">
      <div className="card-container">
        <b>{props.name}</b>
        <p>{props.price}</p>
        <p>{props.description}</p>
        <p>{props.furnitureStyle}</p>
        <p>{props.deliveryTime}</p>
      </div>
    </div>
  )
}

class App extends Component{
  state ={
    ListProducts:[],
    ListFurnitureStyle:[{
      value:1,
      label:''
    }],
    ListDelivery:[{
        value:1,
        label: '1 Week'
      },
      {
        value: 2,
        label: '2 Week'
      },
      {
        value: 3,
        label: '1 Month'
      },
      {
        value: 4,
        label: '& More'
      }
    ],
    SearchValue:'',
    SelectValue:[]
  }

  componentWillMount(){
    this._getApiList();
  }

  _getApiList(){
    let listProduct = {...this.state.ListProducts};
    // let listFurniture = {...this.state.ListFurnitureStyle};
    let listFurniture =[];
    axios.get('http://www.mocky.io/v2/5c9105cb330000112b649af8')
    .then(response => {
      // get list product from API
      listProduct = response.data.products;
      this.setState({ListProducts: listProduct});
      // get list furniture style from API
      for(let i = 0; i<response.data.furniture_styles.length; i++){
        listFurniture.push({
          value: response.data.furniture_styles[i],
          label: response.data.furniture_styles[i]
        })
        this.setState({ListFurnitureStyle: listFurniture}); 
      }
    })
    .catch(err => {
      console.log(err);
    })
  }

  _changeBySearch(e){
    this.setState({SearchValue: e.target.value.substr(0,20)});
  }
  _changeBySelect(e){
    let selectedValue = [] ;
    for(let i=0; i<e.length; i++){
      selectedValue.push(e[i].value)
      this.setState({SelectValue: selectedValue})
      console.log(e[i].value);
      this.renderProductBySearch(e[i].value);
    }

  }

  render(){
    return (
      <div className="">
        <div className="App-header">
          <input className="search" type="text" onChange = {this._changeBySearch.bind(this)} value={this.state.SearchValue} placeholder="Search..."></input>
          <br></br>
          <div className="row">
            <ReactMultiSelectCheckboxes 
              options={this.state.ListFurnitureStyle}
              // value= {this.state.SelectValue[0]||''} 
              onChange={this._changeBySelect.bind(this)}
            />
            <ReactMultiSelectCheckboxes 
              options={this.state.ListDelivery}
              // value= {this.state.SelectValue[0]||''} 
              onChange={this._changeBySelect.bind(this)}
            />

          </div>
        </div>
        {/* <Select isMulti 
          options={this.state.ListFurnitureStyle}
          onChange={this._changeBySelect.bind(this)} 
          // value={this.state.SelectValue}
        /> */}
        {this.renderProductBySearch(this.state.SelectValue)}
      </div>
    );
  }

  renderProductBySearch(furniture){
    let filteredProduct = this.state.ListProducts.filter(
      (ListProducts) => {
        return ListProducts.name.toLowerCase().indexOf(furniture.toLowerCase()) !== -1;
      }
    );
    return(
      <div>
        {filteredProduct.map(item => (
          <Product key = {item.name} 
            name = {item.name} 
            price = {item.price} 
            description = {item.description} 
            deliveryTime = {item.delivery_time} 
            furnitureStyle = {item.furniture_style.map(itemFurniture =>(
              <p key={itemFurniture}>{itemFurniture}</p>
            ))}/>
        ))}
      </div>
    )
  }
}

export default App;
