import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component{
  state ={
    ListProducts:{
      items: []
    },
    SearchValue:'',
  }

  _handleChange(e){
    this.setState({SearchValue: e.target.value.substr(0,20)});
  }

  componentWillMount(){
    this._getApiProductList();
  }

  _getApiProductList(){
    let listProduct = {...this.state.ListProducts};
    this.setState({ListProducts: listProduct})

    axios.get('http://www.mocky.io/v2/5c9105cb330000112b649af8')
    .then(response => {
      listProduct.items = response.data.products;
      console.log(listProduct.items);
      this.setState({ListProducts: listProduct});
    })
    .catch(err => {
      console.log(err);
    })
  }

  render(){
    let filteredProduct = this.state.ListProducts.items;
    return (
      <div className="App">
        <input type="text" onChange={this._handleChange.bind(this)} value={this.state.SearchValue} placeholder="Search..."></input>
        <ul>
          {filteredProduct.map(item => (
            <li key={item.name}>{item.name}</li>
          ))}
        </ul>
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
