import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component{
  state ={
    ListProducts:{
      items: []
    }
  }

  componentWillMount(){
    this._getApiProductList();
  }

  _getApiProductList(){
    let listProduct = {...this.state.ListProducts};
    this.setState({ListProducts: listProduct})

    axios.get('http://www.mocky.io/v2/5c9105cb330000112b649af8')
    .then(response => {
      listProduct.items = response.data.products;
      console.log(listProduct.items);
      this.setState({ListProducts: listProduct});
    })
    .catch(err => {
      console.log(err);
    })
  }

  _handleChange(e){
    let currentList = [];
    let newList = [];
    if(e.target.value != ""){
      currentList = this.state.ListProducts.items;
      newList =  currentList.filter(item => {
        const lc = item.toLowerCase();
        const filter = e.target.value.toLowerCase();
        return lc.includes(filter);
      });
    }
    else{
      newList = this.state.ListProducts.items
    }
  }

  render(){
    return (
      <div className="App">
        <input type="text" onChange="_handleChange" placeholder="Search..."></input>
        <ul>
          {this.state.ListProducts.items.map(item => (
            <li key={item.name}>{item.name}</li>
          ))}
        </ul>
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
