import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
// import { MultiSelect } from '@progress/kendo-react-dropdowns';

function Product(props){
  return(
    <div>
      <b>{props.name}</b>
      <p>{props.price}</p>
      <p>{props.description}</p>
      <p>{props.furnitureStyle}</p>
      <p>{props.deliveryTime}</p>
    </div>
  )
}

class App extends Component{
  state ={
    ListProducts:[],
    ListFurnitureStyle:[],
    SearchValue:'',
  }

  componentWillMount(){
    this._getApiList();
  }

  _getApiList(){
    let listProduct = {...this.state.ListProducts};
    let listFurniture = {...this.state.ListFurnitureStyle};
    axios.get('http://www.mocky.io/v2/5c9105cb330000112b649af8')
    .then(response => {
      // get list product from API
      listProduct = response.data.products;
      this.setState({ListProducts: listProduct});
      // get list furniture style from API
      listFurniture = response.data.furniture_styles;
      this.setState({ListFurnitureStyle: listFurniture});
      console.log(listProduct);
    })
    .catch(err => {
      console.log(err);
    })
  }

  _handleChange(e){
    this.setState({SearchValue: e.target.value.substr(0,20)});
  }

  render(){
    let filteredProduct = this.state.ListProducts.filter(
      (ListProducts) => {
        return ListProducts.name.toLowerCase().indexOf(this.state.SearchValue.toLowerCase()) !== -1;
      }
    );
    return (
      <div className="App">
        <input type="text" onChange = {this._handleChange.bind(this)} value={this.state.SearchValue} placeholder="Search..."></input>
        <MultiSelect data={this.state.ListFurnitureStyle} />
        <ul>
          {filteredProduct.map(item => (
            // <li key={item.name}>{item.name}</li>
            <Product key = {item.name} 
              name = {item.name} 
              price = {item.price} 
              description = {item.description} 
              deliveryTime = {item.delivery_time} 
              furnitureStyle = {item.furniture_style.map(itemFurniture =>(
                <p key={itemFurniture}>{itemFurniture}</p>
              ))}/>
          ))}
        </ul>
        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header> */}
      </div>
    );
  }
}

export default App;
