import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import ReactMultiSelectCheckboxes from 'react-multiselect-checkboxes';
import Select from 'react-select';

function Product(props){
  return(
    <div>
      <b>{props.name}</b>
      <p>{props.price}</p>
      <p>{props.description}</p>
      <p>{props.furnitureStyle}</p>
      <p>{props.deliveryTime}</p>
    </div>
  )
}

class App extends Component{
  state ={
    ListProducts:[],
    ListFurnitureStyle:[{
      value:1,
      label:''
    }],
    SearchValue:'',
    SelectValue:''
  }

  componentWillMount(){
    this._getApiList();
  }

  _getApiList(){
    let listProduct = {...this.state.ListProducts};
    // let listFurniture = {...this.state.ListFurnitureStyle};
    let listFurniture =[];
    axios.get('http://www.mocky.io/v2/5c9105cb330000112b649af8')
    .then(response => {
      // get list product from API
      listProduct = response.data.products;
      this.setState({ListProducts: listProduct});
      // get list furniture style from API
      for(let i = 0; i<response.data.furniture_styles.length; i++){
        listFurniture.push({
          value:response.data.furniture_styles[i],
          label: response.data.furniture_styles[i]
        })
        this.setState({ListFurnitureStyle: listFurniture}); 
      }
    })
    .catch(err => {
      console.log(err);
    })
  }

  _changeBySearch(e){
    this.setState({SearchValue: e.target.value.substr(0,20)});
  }
  _changeBySelect(e){
    console.log(e.label)
    // this.setState({SelectValue: e.target.value})
  }

  render(){
    let filteredProduct = this.state.ListProducts.filter(
      (ListProducts) => {
        return ListProducts.name.toLowerCase().indexOf(this.state.SearchValue.toLowerCase()) !== -1;
      }
    );
    let filteredfurnitureStyle = this.state.ListFurnitureStyle.filter(
      (ListFurnitureStyle) => {
        return ListFurnitureStyle.label.toLowerCase().indexOf(this.state.SelectValue.toLowerCase()) !== -1;
      }
    );
    return (
      <div className="App">
        <input type="text" onChange = {this._changeBySearch.bind(this)} value={this.state.SearchValue} placeholder="Search..."></input>
        {/* <ReactMultiSelectCheckboxes options={this.state.ListFurnitureStyle} onChange={this._changeBySelect}/> */}
        <Select options={this.state.ListFurnitureStyle} isMulti onChange={this._changeBySelect.bind(this)} value={this.state.SelectValue}/>
        <ul>
          {filteredProduct.map(item => (
            <Product key = {item.name} 
              name = {item.name} 
              price = {item.price} 
              description = {item.description} 
              deliveryTime = {item.delivery_time} 
              furnitureStyle = {item.furniture_style.map(itemFurniture =>(
                <p key={itemFurniture}>{itemFurniture}</p>
              ))}/>
          ))}
        </ul>
        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header> */}
      </div>
    );
  }
}

export default App;
